import styled from "styled-components";
import { Titulo } from "../Titulo";
import { DataLivros } from "./DataLivros";
import { SubTitulo } from "../Subtitulo";
import LivroPerfil from "../LivroPerfil";

const ContainerRecomendacao = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    width: auto;
    heigth: 100vh;
    flex-direction: column;
    background-color: #fff;
`

const MostraRecomendacao = styled.div`
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-direction: column;
`

const LivrosRecomendados = styled.div`
    display: flex;
    justify-content: space-around;
    margin-top: 20px; /* Ajuste conforme necessário */
    margin-bottom: 40px;
`

function Recomendacao() {

    return(
        <ContainerRecomendacao>
            <Titulo
                fontSize = '32px'
                color = '#c9c7c9'
                marginTop = '100px'
            > Recomendação de Leitura </Titulo>
            <LivrosRecomendados>
            {
                DataLivros.map( livro => 
                    <MostraRecomendacao>
                        <SubTitulo
                            fontSize = "16px"
                            colorH2 = "#c9c7c9"
                        > {livro.nome} </SubTitulo>
                        <img src={livro.src} alt="img"></img>
                    </MostraRecomendacao>
                )
            }
            </LivrosRecomendados>
            <LivroPerfil
                titulo = "Para você!"
                descricao = "Aprenda JavaScript em 4 horas!"
            />
                        <LivroPerfil
                titulo = "Para você!"
                descricao = "Aprenda JavaScript em 4 horas!"
            />
        </ContainerRecomendacao>
    )
}

export default Recomendacao
