import styled from "styled-components";
import { Titulo } from "../Titulo";
import livro from "../../images/livro2.png"

export default function LivroPerfil (fontSize, colorTexto) {

    const ContainerLivroPerfil = styled.div`
        heigth: 100vh;
        width: 100vw;
    `

    return (
        <ContainerLivroPerfil>
            <Texto>
            <Titulo> Seu perfil! </Titulo>
            <p> Aprenda Java Script em menos de 4 horas </p>
            </Texto>
            <img src={livro} alt="indicacao"></img>
            <button> Saiba Mais </button>
        </ContainerLivroPerfil>
    )
}
