import styled from "styled-components";

const Input = styled.input`
    width: 500px;
    height: 50px;
    border-radius: 20px;
    border: none;
    text-align: center;
    outline: none; //remove contorno do input

    &::placeholder {
        text-align: center;
        font-size: 20px;
    }
`

export default Input
