import styled from "styled-components";
import { Titulo } from "../Titulo";
import livro from "../../images/livro2.png"

export default function LivroPerfil (fontSize, colorTexto) {

    const ContainerLivroPerfil = styled.div`
        heigth: 400px;
        width: 600px;
        display: flex;
        justify-content: center;
        align-items: center;
        padding: 50px;
    `

    const Texto = styled.div`
        display: flex;
        justify-content: center;
        align-items: left;
        flex-direction: column;
    `

    const ImageRecomen = styled.div`
        display: flex;
        justify-content: center;
        align-items: left;
        flex-direction: column;
    `

    const Descricao = styled.p`
        width: 200px;
    `

    return (
        <ContainerLivroPerfil>
            <Texto>
                <Titulo> Seu perfil! </Titulo>
                <Descricao> Aprenda Java Script em menos de 4 horas </Descricao>
            </Texto>
            <ImageRecomen>
                <img src={livro} alt="indicacao"></img>
                <button> Saiba Mais </button>
            </ImageRecomen>
        </ContainerLivroPerfil>
    )
}
