import styled from "styled-components";
import { Titulo } from "../Titulo";
import { DataLivros } from "./DataLivros";
import { SubTitulo } from "../Subtitulo";

const ContainerRecomendacao = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    width: auto;
    heigth: 100vh;
    //flex-direction: column;
    background-color: #fff;
`

const MostraRecomendacao = styled.div`
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-direction: column;
`

function Recomendacao() {

    return(
        <ContainerRecomendacao>
            <Titulo
                fontSize = '32px'
                color = '#cc9c7c9'
                marginTop = '100px'
            > Recomendação de Leitura </Titulo>
            {
                DataLivros.map( livro => 
                    <MostraRecomendacao>
                        <p>Oiiiiii</p>
                        <SubTitulo
                            fontSize = "20px"
                            color = "#c6c6c6"
                        > {livro.name} </SubTitulo>
                        <img src={livro.src} alt="img"></img>
                    </MostraRecomendacao>
                )
            }
        </ContainerRecomendacao>
    )
}

export default Recomendacao
