import "./styles.css"

const opcoes = ['Categorias', 'Favoritos', 'Minha Estante']

function OpcoesHeader() {
    return (
        <ul className="opcoes">
            {
                opcoes.map( (opcao) => (
                    <li className="opcao"> <p> <strong>{opcao}</strong> </p> </li>
                ))
            }
        </ul>
    )
}

export default OpcoesHeader
