import styled from "styled-components";

const Input = styled.input`
    width: 500px;
    height: 50px;
    border-radius: 20px;
    border: none;
    text-align: center;
    outline: none; //remove contorno do input
    font-size: 16px;
    color: #c5c5c5c;
    background-color: rgba(255,255,255,0.5;

    &::placeholder {
        text-align: center;
        font-size: 16px;
    }
`

export default Input
